import React, { memo } from "react";
import { MDBCarouselItem } from "mdb-react-ui-kit";
import style from "./Videojuego.module.css";

const VideojuegoBanner = ({ id, caratula, captura }) => {
    return(
        <>{String(id) === "0" ? (
            <MDBCarouselItem
                id={style.item}
                className="w-100 d-block"
                itemId={id+1}
                src={caratula}
                alt="CarÃ¡tula"
                height="300vh"
            />
        ) : (
            <MDBCarouselItem
                id={style.item}
                className="w-100 d-block"
                itemId={id+1}
                src={captura}
                alt="Captura"
                height="300vh"
            />
        )}</>
    );
};

export default memo(VideojuegoBanner);
