                <div className={style.modal}>
                    <header className={style.modalHeader}>
                        <h2 className={style.modalHeaderTitle}>TÃ­tulo</h2>
                        <button className={style.close}>Close</button>
                    </header>

                    <main className={style.modalContent}>
                        Contenido
                    </main>

                    <footer className={style.modalFooter}>
                        <button className={style.modalClose}>Aceptar</button>
                    </footer>
                </div>
            </div>
        : null}</>
    );
};

export default Mensaje;
