import React from "react";
import ReactDOM from "react-dom";
import style from "./Mensaje.module.scss";
import buttonClose from "./media/close.svg";

const Mensaje = ({ show, close, title, children }) => {
    const change = () => {
        status = show
    };

    return ReactDOM.createPortal(
        <><div className={`${style.modalContainer} ${show ? style.show : ""}`} onClick={() => close() && change()}>
            <div className={style.modal} onClick={(e) => e.stopPropagation()}>
                <header className={style.modalHeader}>
                    <h1 className={style.modalHeaderTitle}>{title}</h1>
                    <button className={style.close} onClick={() => close()}>
                        <img src={buttonClose} alt="close"></img>
                    </button>
                </header>

                <main className={style.modalContent}>
                    <h3>{children}</h3>
                </main>

                <footer className={style.modalFooter}>
                    <button className={style.modalClose} onClick={() => close()}>Aceptar</button>
                </footer>
            </div>
        </div></>,
        document.getElementById("mensaje")
    );
};

export default Mensaje;

export var status = false;
