import React, { useState } from "react";
import Cookies from "universal-cookie";
import './VideojuegoForm.css';
import * as UsuarioServer from '../Usuario/UsuarioServer';


const CarritoBtn = ({ videojuego }) => {
    const cookies = new Cookies();
    const [usuario] = useState({  correo: cookies.get("correo") });

    

    return (
        <div className="col-sm-12 col-md-6 col-lg-6" id="contenedor_botones">
            <button  className="btn btn-success btn-lg" onClick={addCart()}>Agregar a carrito (${videojuego.precio.valor})</button>
        </div>
    );

    
};

export default CarritoBtn;
