import React from "react";
import style from "./Filtro.module.css";

const FiltroItem = ({ filtro }) => {
    return(
        <>{String(filtro.id) === "0" ? (
            <aside id={style.categoria}>
                <div>
                    <h4 id={style.text}><u>{filtro.nombre}</u></h4>

                    <form>
                        <div>
                            {filtro.contenido.map(c => (
                                <label id={style.radLabel}>
                                    <input type="radio" id={style.radInput} name="rad" value={c} />
                                    <div id={style.radDesign}></div>
                                    <div id={style.radText}>{c}</div>
                                </label>
                            ))}
                        </div>

                        <button type="submit" id={style.boton}>
                            Aplicar Filtro
                        </button>
                    </form>
                </div>
            </aside>
        ) : (
            <option>{filtro.nombre}</option>
        )}</>
    );
};

export default FiltroItem;
