import React from "react";
import ReactDOM from "react-dom";
import style from "./Mensaje.module.scss";

const Mensaje = ({ show, close, title, children }) => {
    return ReactDOM.createPortal(
        <>{show ? (
            <div className={style.modalContainer `${show ? "show" : ""}`} onClick={() => close()}>
                <div className={style.modal} onClick={(e) => e.stopPropagation()}>
                    <header className={style.modalHeader}>
                        <h2 className={style.modalHeaderTitle}>{title}</h2>
                        <button className={style.close} onClick={() => close()}>Close</button>
                    </header>

                    <main className={style.modalContent}>{children}</main>

                    <footer className={style.modalFooter}>
                        <button className={style.modalClose} onClick={() => close()}>Aceptar</button>
                    </footer>
                </div>
            </div>
        ) : null}</>,
        document.getElementById("mensaje")
    );
};

export default Mensaje;
