import React, { useEffect, useState } from "react";
import * as WishListVideojuegoServer from "./Relacion/WishListVideojuegoServer";
import * as VideojuegoServer from "../Videojuego/VideojuegoServer";

const WishListItem = ({ wishlist }) => {
    const [videojuegos, setVideojuegos] = useState([]);

    const getVideojuego = async () => {
        try{
            const data = await getContenido();
            setVideojuegos(data.Videojuegos);
        }catch(error){
            console.log(error);
        }
    };

    const getContenido = async () => {
        const dataWishListVideojuego = await (await WishListVideojuegoServer.getWishListVideojuegoByIdWishList(wishlist.id)).json();
        let idVideojuegos = "";

        if(dataWishListVideojuego != null)
            await dataWishListVideojuego.WishListVideojuegos.forEach(wv => {
                idVideojuegos += wv.idVideojuego + ",";
            });

        return await (await VideojuegoServer.getVideojuegosByIdVideojuegos(idVideojuegos)).json();
    };

    useEffect(() => {
        getVideojuego();
        // eslint-disable-next-line
    }, []);

    return(
        <>{videojuegos.length > 0 ? (
            videojuegos.map(videojuego => (
                <><a href={`/videojuego/${videojuego.id}`} className="list-group-item list-group-item-action d-flex gap-3 py-3" aria-current="true">
                    <img src={videojuego.caratula} alt="videojuegoCaratula" width="64" height="64" class="rounded-circle flex-shrink-0" />

                    <div className="d-flex gap-2 w-100 justify-content-between">
                        <div>
                            <h6 className="mb-0"><strong>{videojuego.nombre}</strong></h6>
                            <p className="mb-0 opacity-75"><strong>Género</strong> {videojuego.genero}</p>
                        </div>

                        <small className="opacity-50 text-nowrap"><strong>{videojuego.precio.valor} {videojuego.precio.simbolo}</strong></small>
                    </div>
                </a></>
        ))) : (
            <><a href="#" className="list-group-item list-group-item-action d-flex gap-3 py-3" aria-current="true">
                <div className="d-flex gap-2 w-100 justify-content-between">
                    <h6 class="mb-0"><strong>No hay Videojuegos por mostrar</strong></h6>
                    <p class="mb-0 opacity-75">Agregué alguno para mostrarlo aquí</p>
                </div>
            </a></>
        )}</>
    );
};

export default WishListItem;
